import * as actionsTypes from './constants';

export function logIn(url, data) {
  return {
    type: actionsTypes.LOG_IN,
    payload: {
      url,
      data
    }
  };
}

export function receiveLogIn(data) {
  return {
    type: actionsTypes.LOG_IN_RECIEVED,
    payload: {
      data
    }
  };
}

export function cancelLogIn(data) {
  return {
    type: actionsTypes.LOG_IN_CANCELED,
    payload: {
      data
    }
  };
}



