import * as actionsTypes from './constants';

export function logIn(url, data) {
  return {
    type: actionsTypes.LOG_IN,
    payload: {
      url,
      data
    }
  };
}

export function receiveLogIn(data) {
  return {
    type: actionsTypes.LOG_IN_RECIEVED,
    payload: {
      data
    }
  };
}

export function cancelLogIn() {
  return {
    type: actionsTypes.LOG_IN_CANCELED
  };
}

export function logOut() {
  return {
    type: actionsTypes.LOG_OUT
  };
}

export function userMe() {
  return {
    type: actionsTypes.USER_ME
  };
}

export function receiveUserMe(data) {
  return {
    type: actionsTypes.USER_ME_RECIEVED,
    payload: {
      data
    }
  };
}


